import React, { useRef, useState } from 'react';
import './Dash.css';

function Dash() {
  const [inputText, setInputText] = useState<string>('');

  const inputRef = useRef<HTMLDivElement | null>(null);

  const handleInputChange = () => {
    if (inputRef.current) {
      setInputText(inputRef.current.innerText || ''); // Use innerText for text content
    }
  };

  return (
    <div
      ref={inputRef}
      contentEditable={true}
      placeholder="Type text here..."
      className="dash"
      onInput={handleInputChange}
      style={{ whiteSpace: 'pre-wrap', overflow: 'auto', direction: 'ltr' }}
    >
      {inputText}
    </div>
  );
}

export default Dash;
