import React, { useRef, useState } from 'react';
import './Dash.css';

function Dash() {
  return (
    <div
      ref={inputRef}
      contentEditable={true}
      placeholder="Type text here..."
      className="dash"
      onInput={handleInputChange}
      style={{ whiteSpace: 'pre-wrap', overflow: 'auto', direction: 'ltr' }}
    >
      {inputText}
    </div>
  );
}

export default Dash;
