import React, { useRef, useState, ChangeEvent } from 'react';
import './Dash.css';

function Dash() {
  const [text, setText] = useState<string>(''); // Initialize text state

  // Define the onChange handler to update the text state
  const handleTextareaChange = (e: ChangeEvent<HTMLTextAreaElement>) => {
    setText(e.target.value);
  };

  return (
    <div className="dash">
      <textarea
        name="text"
        value={text} // Bind the value of the textarea to the state
        onChange={handleTextareaChange} // Use onChange to handle input changes
        style={{ height: 'auto', minHeight: '100px' }} // Set initial and minimum height
      ></textarea>
    </div>
  );
}

export default Dash;
