      animate={{ opacity: 0, scale: 1.2 }}
      exit={{ opacity: 0 }}
      className='iconic'>
      <div className="mid-panel">

        <motion.div
          transition={{ duration: 1 }}
          initial={{ opacity: 0, transform: 'translateY(-40px)' }}
