import React, { useRef, useState } from 'react';
import './Dash.css';

function Dash() {
  const [inputText, setInputText] = useState<string>('');

  const handleInputChange = (e) => {
    setInputText(e.target.value);
  };

  const calculateRowCount = (text) => {
    const lineHeight = 16; // Adjust this value based on your font and styling
    const minHeight = 32; // Minimum height for one row
    const lines = text.split('\n').length;
    const rowCount = Math.max(minHeight / lineHeight, lines);
    return rowCount;
  };

  const rowCount = calculateRowCount(inputText);

  return (
    <div className="dash">
      <textarea
        rows={rowCount}
        placeholder="Type text here..."
        value={inputText}
        onChange={handleInputChange}
        className="textarea"
      ></textarea>
    </div>
  );
}

export default Dash;
