import React from 'react';
import './App.css';
import './components/TopBar/TopBar'
import './components/Dash/Dash'
import TopBar from './components/TopBar/TopBar';
import Dash from './components/Dash/Dash';

function App() {
  const [textareaContent, setTextareaContent] = useState('');

  const handleTextChange = (newText) => {
    setTextareaContent(newText);
  };

  const handleButtonClick = (textContent) => {
    // Print the content to the console when the button is pressed
    console.log(textContent);
  };

  return (
    <div className="App">
      <header className="App-header">
        <div className="TopBar">
          <TopBar onButtonClick={handleButtonClick} textContent={textareaContent} />
        </div>
        <div className="Dash">
          <Dash onTextChange={handleTextChange} />
        </div>
      </header>
    </div>
  );
}

export default App;
