// src/blog/BlogUtils.ts
import React, { lazy, LazyExoticComponent } from 'react';

// Utility function to dynamically import blog components
export const importAllBlogComponents = (context: __WebpackModuleApi.RequireContext) => {
    const components: Record<string, LazyExoticComponent<React.ComponentType<any>>> = {};
    context.keys().forEach((key) => {
        const componentName = key.replace('./', '').replace('.tsx', '').replace('.ts', '');
        components[componentName] = lazy(() => context(key));
    });
    return components;
};

// Export all blog components as a Record
export const allBlogComponents = importAllBlogComponents(
    require.context('./blogContent', false, /\.(tsx|ts)$/),
);
