import React, { useEffect, useRef } from 'react';
import './SquarePanel.css';
import { motion } from 'framer-motion';

function SquarePanel() {
  const squarePanelRef = useRef(null);
  const [animate, setAnimate] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const element = squarePanelRef.current;
      if (element) {
        const rect = element.getBoundingClientRect();
        const windowHeight = window.innerHeight || document.documentElement.clientHeight;

        if (rect.top < windowHeight) {
          // Element is in the viewport, add animation class
          setAnimate(true);
        }
      }
    };

    window.addEventListener('scroll', handleScroll);
    handleScroll(); // Check on initial load
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div className="square-panel" ref={squarePanelRef}>
      <motion.div className={`motion-div-text ${animate ? 'animate' : ''}`}>
        <div className='title'>
          Hello!
        </div>
      </motion.div>

      <motion.div className={`motion-div ${animate ? 'animate' : ''}`}>
        <div className="smaller-panel">
          <p>This is a smaller panel Tpanelpanelhis is a smaller panel.</p>
          <p>This is a smaller panel.</p>
          <p>This is a smaller panel.</p>
          <p>This is a smaller panel.</p>
          <p>This is a smaller panel.</p>
        </div>
      </motion.div>
    </div>
  );
}

export default SquarePanel;
