import React, { } from 'react';
import './SquarePanel.css';
import { motion, useScroll } from 'framer-motion';

function SquarePanel() {
  const { scrollYProgress } = useScroll();

  // Define a custom animation duration (in seconds)
  const animationDuration = 2; // Adjust this value as needed

  // Define a custom background color
  const backgroundColor = "#3498db"; // Change to your desired background color

  const containerStyle = {
    background: backgroundColor,
    transition: `background ${animationDuration}s ease`, // Apply the transition to the background
  };

  return (
    <motion.div
      className="square-panel"
      style={containerStyle}
      initial={{ opacity: 0, scale: 0.5 }}
      animate={{ opacity: 1, scale: 1 }}
      transition={{ duration: animationDuration }}
    >
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        viewport={{ once: true }}
        transition={{ duration: animationDuration }}
      >
        <div className='title'>
          Hello!
        </div>
      </motion.div>

      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        viewport={{ once: true }}
        transition={{ duration: animationDuration }}
      >
        <div className="smaller-panel">
          <p>This is a smaller panel Tpanelpanelhis is a smaller panel.</p>
          <p>This is a smaller panel.</p>
          <p>This is a smaller panel.</p>
          <p>This is a smaller panel.</p>
          <p>This is a smaller panel.</p>
        </div>
      </motion.div>
    </motion.div>
  );
}

export default SquarePanel;
