import React, { } from 'react';
import './SquarePanel.css';
import { motion, useScroll } from 'framer-motion';

function SquarePanel() {

  const { scrollYProgress } = useScroll();

  return (
    <motion.div
      initial={{ opacity: 0, scale: 0.5 }} // Start with a smaller size
      animate={{ opacity: 1, scale: 1 }} // Enlarge to the original size
      transition={{ duration: 2 }}
      viewport={{ once: true }}
    >
      <div className="square-panel">

        <motion.div initial={{ opacity: 0, transform: 'translateY(-40px)' }}
          whileInView={{ opacity: 1, transform: 'translateY(0)' }}
          viewport={{ once: true }}>
          <div className='title'>
            Hello!
          </div>
        </motion.div>


        <motion.div initial={{ opacity: 0, transform: 'translateY(-60px)' }}
          whileInView={{ opacity: 1, transform: 'translateY(0)' }}
          transition={{ duration: 2 }}
          viewport={{ once: true }}>
          <div className="smaller-panel">
            <p>This is a smaller panel Tpanelpanelhis is a smaller panel.</p>
            <p>This is a smaller panel.</p>
            <p>This is a smaller panel.</p>
            <p>This is a smaller panel.</p>
            <p>This is a smaller panel.</p>
          </div>
        </motion.div>
      </div>
    </motion.div>
  );
}

export default SquarePanel;
