          <motion.div initial={{opacity: 0}}
              whileInView={{opacity: 1}}
              transition={{duration: 2.3, delay: 1}}
              viewport={{ once: true}}>
                <p> Let me tell you a little bit about myself.</p>
              </motion.div>
          <p className='show-p'>This is a smaller panel.</p>
          <div className='inside-panel'>
            <motion.div initial={{opacity: 0}}
              whileInView={{opacity: 1}}
