// src/blog/BlogUtils.ts
import React, { lazy, LazyExoticComponent } from 'react';

// Define a custom Webpack context type
interface WebpackContext {
  (id: string): LazyExoticComponent<React.ComponentType<any>>;
  keys(): string[];
  <T>(id: string): T;
  resolve(id: string): string;
  context: (directory: string, useSubdirectories?: boolean, regExp?: RegExp) => {
    keys(): string[];
    <T>(id: string): T;
    resolve(id: string): string;
  };
}

// Explicitly provide the type for require
const requireContext = (require as any).context as WebpackContext;

// Utility function to dynamically import blog components
export const importAllBlogComponents = (context: WebpackContext) => {
  const components: Record<string, LazyExoticComponent<React.ComponentType<any>>> = {};
  context.keys().forEach((key) => {
    const componentName = key.replace('./', '').replace('.tsx', '').replace('.ts', '');
    // Wrap the lazy function with React.lazy and use dynamic import
    const importFunction = () => import(/* webpackChunkName: "[request]" */ `${key}`);
    components[componentName] = React.lazy(importFunction);
  });
  return components;
};

// Export all blog components as a Record
export const allBlogComponents = importAllBlogComponents(requireContext);
